<template>
    <a-card>
        <div class="flex justify-end">
            <a-button @click="() => $inertia.visit(route('finance.dashboard'))" class="mb-2">
                <RollbackOutlined />
                Back to Dashboard
            </a-button>
        </div>
        <a-table :dataSource="data" :columns="columns" bordered size="small">
            <template v-slot:bodyCell="{ column, record }">
                <template v-if="column.dataIndex === 'View'">
                    <a-button type="primary" @click="approvedSpexGcDetails(record)">
                        <PicLeftOutlined />View
                    </a-button>
                </template>
            </template>
        </a-table>
    </a-card>
    <a-drawer :placement="placement" :height="520" :closable="false" :open="open" @close="onClose">
        <a-tabs v-model:activeKey="activeKey">
            <a-tab-pane key="1" :tab="'Special External GC Request # ' + selectedData.spexgc_num">
                <a-row :gutter="[16,16]">
                    <a-col :span="6">
                        <a-card>
                            <a-form-item label="Date Requested">
                                <a-input v-model:value="selectedData.dateRequested" readonly></a-input>
                            </a-form-item>
                            <a-form-item label="Date Needed">
                                <a-input v-model:value="selectedData.dateNeeded" readonly></a-input>
                            </a-form-item>
                            <a-form-item label="Document">
                                <a-image style="height: 200px;"
                                    src="data:image/jpeg;base64,"></a-image>
                            </a-form-item>
                        </a-card>
                    </a-col>
                    <a-col :span="6">
                        <a-card>
                            <a-form-item label="Date Validity">
                                <a-input v-model:value="selectedData.dateNeeded" readonly></a-input>
                            </a-form-item>
                        </a-card>
                    </a-col>
                    <a-col :span="6"></a-col>
                    <a-col :span="6"></a-col>
                </a-row>
                {{ selectedData }}
            </a-tab-pane>
            <a-tab-pane key="2" tab="Barcodes" force-render>Content of Tab Pane 2</a-tab-pane>
        </a-tabs>
    </a-drawer>

</template>

<script>
import AuthenticatedLayout from "@/Layouts/AuthenticatedLayout.vue";
import axios from "axios";


export default {
    layout: AuthenticatedLayout,

    props: {
        data: Object,
        columns: Object,
    },
    data() {
        return {
            details: '',
            selectedData: null,
            open: false,
            placement: 'bottom',
            activeKey: '1'
        }
    },
    methods: {
        approvedSpexGcDetails(data) {
            axios.get(route('finance.approvedGc.selected.approved'), {
                params: {
                    id: data.spexgc_id
                }
            }).then((response) => {
                this.open = true
                this.selectedData = response.data.data[0]
            });
        },
        onClose() {
            this.open = false
        }

    },
    computed: {
        paymentTypeText() {
            const paymentMap = {
                '1': 'Cash',
                '2': 'Check',
                '3': 'JV',
                '4': 'AR',
                '5': 'On Account'
            };
            return paymentMap[this.selectedGcData[0]['spexgc_paymentype']] || '';
        }
    }
}

</script>
